define(['jquery','underscore'],function($,_){'use strict';return function(data){if(data.excludedBrowsers){let _excludedBrowsers=data.excludedBrowsers.replace(/","/g,"|");const regexPattern=new RegExp('/'+_excludedBrowsers+'/i');if(regexPattern.test(navigator.userAgent)){document.getElementById(data.parentElement).remove();}else{initBackToTop();}}else{initBackToTop();}
function initBackToTop(){$(document).ready(function(){$(window).on('scroll',_.debounce(function(){if($(this).scrollTop()>window.innerHeight&&document.body.scrollHeight>=(window.innerHeight*4)){$('#back-to-top').fadeIn();}else{$('#back-to-top').fadeOut();}},250));$('#back-to-top').click(function(){$('html, body').animate({scrollTop:0},1000);const focusableElement=document.querySelector('button, a, input, select, textarea, [tabindex]:not([tabindex="-1"])');focusableElement.focus({preventScroll:true,});return false;});});}};});