const productImageContainer=document.querySelectorAll(".product-image-wrapper");if(productImageContainer.length){productImageContainer.forEach(function(imageContainer){const productImage=imageContainer.querySelector(".product-image-photo");if(productImage){productImage.addEventListener("load",function(){const productImageContainer=imageContainer;if(productImageContainer.classList.contains("product-image-wrapper--loading")){productImageContainer.classList.remove("product-image-wrapper--loading");}});}});}