define(['jquery','mage/validation'],function($){'use strict';$.widget('redbox.validation',$.mage.validation,{_create:function(){this._super();},listenFormValidateHandler:function(event,validation){var errorList,successList;errorList=validation.errorList;if(errorList.length){for(var i=0;errorList[i];i++){var formElement=errorList[i].element;$(formElement).parent().removeClass('valid').addClass('error');}}
successList=validation.successList;if(successList.length){$.each(successList,function(){$(this).removeAttr('aria-describedby').removeAttr('aria-invalid');$(this).parent().removeClass('error').addClass('valid');});}}});return $.redbox.validation;});